// Unified Input & Context Processing - Optimized for v3.0
const startTime = Date.now();
console.log('=== UNIFIED INPUT & CONTEXT PROCESSOR v3.0 ===');

try {
  const input = $input.first();
  console.log('Input keys:', Object.keys(input?.json || {}));

  let normalizedData = {};

  // Process input data (replaces Enhanced Input Processor)
  if (input.json.chatInput) {
    // Test interface mode
    const testSessionId = require('crypto').randomBytes(16).toString('hex');
    normalizedData = {
      input: input.json.chatInput || '',
      userId: 'test-user',
      channelId: 'test-channel',
      sessionId: testSessionId,
      platform: 'n8n-chat-test',
      isTestMode: true
    };
  } else {
    // Discordant webhook mode
    let payload = {};

    if (input.json.body && typeof input.json.body === 'object') {
      payload = input.json.body;
    } else if (input.json.body && typeof input.json.body === 'string') {
      try {
        payload = JSON.parse(input.json.body);
      } catch (e) {
        payload = input.json || {};
      }
    } else {
      payload = input.json || {};
    }

    normalizedData = {
      input: payload.message || payload.content || '',
      userId: payload.userId || 'unknown-user',
      channelId: payload.channelId || 'unknown-channel',
      sessionId: `${payload.userId || 'unknown'}-${payload.channelId || 'unknown'}`,
      platform: 'discordant',
      isTestMode: false,
      userName: payload.userName || 'Unknown User',
      serverId: payload.serverId || 'unknown-server'
    };
  }

  normalizedData.timestamp = new Date().toISOString();
  normalizedData.processingStartTime = startTime;

  console.log('=== UNIFIED PROCESSOR SUCCESS ===');
  console.log('Processing time:', Date.now() - startTime, 'ms');

  return normalizedData;

} catch (error) {
  console.error('=== UNIFIED PROCESSOR ERROR ===');
  console.error('Error details:', error.message);

  return {
    input: 'Error processing input: ' + error.message,
    userId: 'error-user',
    channelId: 'error-channel',
    sessionId: 'error-session',
    platform: 'error',
    timestamp: new Date().toISOString(),
    isTestMode: false,
    errorOccurred: true,
    errorMessage: error.message,
    processingStartTime: startTime
  };
}

// Enhanced Context Formatter with Channel History Integration
const startTime = Date.now();
console.log('=== CONTEXT FORMATTER v3.0 ===');

try {
  const inputData = $('Unified Input & Context Processor').first().json;
  const channelHistory = $('Enhanced Channel History Query').all();

  console.log('Channel history items:', channelHistory.length);
  console.log('Input data keys:', Object.keys(inputData));

  // Process context data (replaces Format Context)
  let conversationHistory = 'No previous conversation in this channel';

  if (channelHistory && channelHistory.length > 0) {
    const validMessages = channelHistory.filter(item => 
      item.json?.content?.trim()?.length > 0
    );

    if (validMessages.length > 0) {
      conversationHistory = validMessages
        .reverse()
        .map(item => {
          const userName = item.json.user_name || item.json.user_id;
          return `${userName}: ${item.json.content}`;
        })
        .join('\n\n');
    }
  }

  const now = new Date();
  const messageCount = channelHistory?.filter(item => 
    item.json?.content?.trim()
  ).length || 0;

  const result = {
    ...inputData,
    conversationHistory,
    currentDate: now.toISOString().split('T')[0],
    currentTime: now.toLocaleTimeString('en-US', { timeZone: 'America/New_York' }),
    messageCount,
    processingTime: Date.now() - startTime,
    coldStartRecovery: messageCount === 0 && channelHistory && channelHistory.length === 0
  };

  console.log('=== CONTEXT FORMATTER SUCCESS ===');
  console.log('Message count:', result.messageCount);
  console.log('Processing time:', result.processingTime, 'ms');

  return result;

} catch (error) {
  console.error('=== CONTEXT FORMATTER ERROR ===');
  console.error('Error details:', error.message);

  const inputData = $('Unified Input & Context Processor').first().json;
  return {
    ...inputData,
    conversationHistory: 'Channel history temporarily unavailable',
    currentDate: new Date().toISOString().split('T')[0],
    currentTime: new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' }),
    messageCount: 0,
    errorOccurred: true,
    errorMessage: error.message,
    processingTime: Date.now() - startTime
  };
}

