// Streamlined Response Formatter - v3.0 Optimization
const startTime = Date.now();
console.log('=== STREAMLINED RESPONSE FORMATTER v3.0 ===');

try {
  const aiResponse = $('Enhanced AI Agent v3.0').first().json;
  const contextData = $('Context Formatter').first().json;

  console.log('AI Response keys:', Object.keys(aiResponse || {}));
  console.log('Context data available:', !!contextData);

  // Optimized response extraction with ordered fallbacks
  const extractResponse = (response) => {
    const sources = [
      () => response?.output,
      () => response?.text,
      () => response?.content,
      () => response?.choices?.[0]?.message?.content,
      () => typeof response === 'string' && response.length > 10 ? response : null
    ];

    for (const getResponse of sources) {
      const result = getResponse();
      if (result?.trim()?.length > 0) {
        return result;
      }
    }
    return null;
  };

  let responseText = extractResponse(aiResponse);

  // Generate contextual fallback if needed
  if (!responseText) {
    const userMessage = contextData?.input || 'your request';
    const currentTime = new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

    responseText = `ü§ñ **AI Assistant v3.0 Response**\n\nI received your request: "${userMessage}"\n\nI'm experiencing technical difficulties with the advanced processing tools right now.\n\n**What I Can Still Help With:**\n‚Ä¢ General questions and conversation\n‚Ä¢ Basic analysis and recommendations\n‚Ä¢ System status updates\n\n**Time**: ${currentTime}\n**Status**: Core AI operational, advanced tools temporarily unavailable\n\n**Optimization**: v3.0 streamlined processing active`;
  }

  const result = contextData?.isTestMode ? {
    output: responseText,
    testMode: true,
    timestamp: new Date().toISOString(),
    processingTime: Date.now() - startTime,
    version: '3.0'
  } : {
    content: responseText,
    metadata: {
      userId: contextData?.userId || 'unknown',
      channelId: contextData?.channelId || 'unknown',
      sessionId: contextData?.sessionId || 'unknown',
      platform: contextData?.platform || 'discordant',
      messageCount: contextData?.messageCount || 0,
      timestamp: new Date().toISOString(),
      responseSource: 'n8n-workflow-v3.0',
      processingTime: Date.now() - startTime,
      responseLength: responseText?.length || 0,
      optimizationLevel: 'v3.0-streamlined'
    }
  };

  console.log('=== RESPONSE FORMATTER SUCCESS ===');
  console.log('Response length:', responseText?.length || 0);
  console.log('Processing time:', Date.now() - startTime, 'ms');

  return result;

} catch (error) {
  console.error('=== RESPONSE FORMATTER ERROR ===');
  console.error('Error details:', error.message);

  const currentTime = new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

