// Enhanced Input Processor Update for v5.1
// This code handles external calls by routing them to the system channel

// Add this to your Enhanced Input Processor node in n8n

const input = $input.first();
let normalizedData = {};

// System channel configuration - matches your Discord setup
const SYSTEM_CHANNEL_ID = 'clwtz9i7m0008n9n01vhep8zt'; // Your system channel ID
const SYSTEM_SERVER_ID = 'a90f1d41-12a9-4586-b9a4-a513d3bd01d9'; // Your server ID

try {
  if (input.json.chatInput) {
    // Test interface mode
    const testSessionId = require('crypto').randomBytes(16).toString('hex');
    normalizedData = {
      input: input.json.chatInput || '',
      userId: 'test-user',
      channelId: SYSTEM_CHANNEL_ID, // Use system channel for tests
      sessionId: testSessionId,
      platform: 'n8n-chat-test',
      timestamp: new Date().toISOString(),
      isTestMode: true,
      isExternalCall: true,
      userName: 'Test User',
      serverId: SYSTEM_SERVER_ID
    };
  } else {
    // Webhook mode
    let payload = {};

    // Parse payload from various sources
    if (input.json.body && typeof input.json.body === 'object') {
      payload = input.json.body;
    } else if (input.json.body && typeof input.json.body === 'string') {
      try {
        payload = JSON.parse(input.json.body);
      } catch (e) {
        payload = input.json || {};
      }
    } else {
      payload = input.json || {};
    }

    // Detect if this is an external/unknown call
    const isExternalCall = !payload.channelId || 
                          payload.channelId === 'unknown-channel' ||
                          payload.channelId === 'unknown' ||
                          payload.serverId === 'unknown-server' ||
                          payload.platform === 'external-api' ||
                          payload.platform === 'vapi';

    // Route external calls to system channel
    const channelId = isExternalCall ? SYSTEM_CHANNEL_ID : payload.channelId;
    const serverId = isExternalCall ? SYSTEM_SERVER_ID : payload.serverId;

    // Build normalized data
    normalizedData = {
      input: payload.message || payload.content || '',
      userId: payload.userId || 'external-user',
      channelId: channelId,
      sessionId: payload.sessionId || `${payload.userId || 'external'}-${channelId}`,
      platform: payload.platform || 'discordant',
      timestamp: payload.timestamp || new Date().toISOString(),
      isTestMode: false,
      isExternalCall: isExternalCall,
      userName: payload.userName || (isExternalCall ? 'External API User' : 'Unknown User'),
      serverId: serverId,
      // Preserve original values for logging
      originalChannelId: payload.channelId,
      originalServerId: payload.serverId,
      externalSource: isExternalCall ? (payload.platform || 'api') : null
    };

    console.log(`Processing ${isExternalCall ? 'EXTERNAL' : 'INTERNAL'} call`);
    console.log(`Routing to channel: ${channelId}`);
  }

  normalizedData.processingStartTime = Date.now();

  return normalizedData;

} catch (error) {
  console.error('Input processor error:', error.message);

  // Fallback to system channel on error
  return {
    input: 'Error processing input: ' + error.message,
    userId: 'error-user',
    channelId: SYSTEM_CHANNEL_ID,
    sessionId: 'error-session',
    platform: 'error',
    timestamp: new Date().toISOString(),
    isTestMode: false,
    isExternalCall: true,
    errorOccurred: true,
    errorMessage: error.message,
    userName: 'Error Handler',
    serverId: SYSTEM_SERVER_ID
  };
}

// Enhanced Format Context with cold start resilience
console.log('=== FORMAT CONTEXT NODE DEBUG ===');
const startTime = Date.now();

try {
  // Get channel history from Discordant message table
  const channelHistory = $('Load Channel History').all();
  const inputData = $('Enhanced Input Processor').first().json;

  console.log('Channel history items:', channelHistory.length);
  console.log('Input data keys:', Object.keys(inputData));
  console.log('Processing time so far:', Date.now() - startTime, 'ms');

  // Handle cold start scenarios gracefully
  if (!channelHistory || channelHistory.length === 0) {
    console.log('WARNING: No channel history found for channel:', inputData.channelId);
    console.log('This might be due to cold start delays or empty channel');
  } else {
    console.log('Sample channel history:', JSON.stringify(channelHistory.slice(0, 2), null, 2));
  }

  // Format conversation history from Discordant messages with better error handling
  let conversationHistory = 'No previous conversation in this channel';

  try {
    if (channelHistory && channelHistory.length > 0) {
      const validMessages = channelHistory.filter(item => {
        return item.json && 
               item.json.content && 
               typeof item.json.content === 'string' && 
               item.json.content.trim().length > 0;
      });

      if (validMessages.length > 0) {
        conversationHistory = validMessages
          .reverse()
          .map(item => `${item.json.user_id}: ${item.json.content}`)
          .join('\n\n');
      }
    }
  } catch (historyError) {
    console.error('Error processing conversation history:', historyError.message);
    conversationHistory = 'Error loading conversation history (cold start recovery)';
  }

  const now = new Date();
  const currentDate = now.toISOString().split('T')[0];
  const currentTime = now.toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

  const messageCount = channelHistory ? channelHistory.filter(item => item.json && item.json.content && item.json.content.trim()).length : 0;

  const result = {
    ...inputData,
    conversationHistory,
    currentDate,
    currentTime,
    messageCount,
    processingTime: Date.now() - startTime,
    coldStartRecovery: messageCount === 0 && channelHistory && channelHistory.length === 0
  };

  console.log('=== FORMAT CONTEXT SUCCESS ===');
  console.log('Result keys:', Object.keys(result));
  console.log('Message count:', result.messageCount);
  console.log('Channel:', inputData.channelId);
  console.log('Conversation history length:', result.conversationHistory.length);
  console.log('Total processing time:', result.processingTime, 'ms');

  return result;

} catch (error) {
  console.error('=== FORMAT CONTEXT ERROR ===');
  console.error('Error details:', error.message);
  console.error('Error stack:', error.stack);
  console.error('Processing time before error:', Date.now() - startTime, 'ms');

  const inputData = $('Enhanced Input Processor').first().json;
  return {
    ...inputData,
    conversationHistory: 'Channel history temporarily unavailable (cold start/initialization delay)',
    currentDate: new Date().toISOString().split('T')[0],
    currentTime: new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' }),
    messageCount: 0,
    errorOccurred: true,
    errorMessage: error.message,
    processingTime: Date.now() - startTime,
    coldStartIssue: true
  };
}

