    return {
      ...inputData,
      channelId: inputData.channelId,
      channelCreated: false,
      channelExists: true,
      channelManagerStatus: 'PASSTHROUGH'
    };
  }

  const channelName = inputData.channelName;
  console.log(`[CHANNEL_MANAGER] Managing channel: ${channelName}`);

  // Check if channel was found in database
  if (channelLookupResults && channelLookupResults.length > 0 && channelLookupResults[0].json.channelId) {
