        name: foundChannel.channelName,
        type: foundChannel.type,
        serverId: foundChannel.serverId
      },
      channelManagerStatus: 'DATABASE_FOUND'
    };
  } else {
    // Channel not found in database, use fallback
    console.log(`[CHANNEL_MANAGER] Channel ${channelName} not found in database`);
