// Enhanced Format Context with cold start resilience
console.log('=== FORMAT CONTEXT NODE DEBUG ===');
const startTime = Date.now();

try {
  // Get channel history from Discordant message table
  const channelHistory = $('Load Channel History').all();
  const inputData = $('Input Processor').first().json;

  console.log('Channel history items:', channelHistory.length);
  console.log('Input data keys:', Object.keys(inputData));
  console.log('Processing time so far:', Date.now() - startTime, 'ms');

  // Handle cold start scenarios gracefully
  if (!channelHistory || channelHistory.length === 0) {
    console.log('WARNING: No channel history found for channel:', inputData.channelId);
    console.log('This might be due to cold start delays or empty channel');
  } else {
    console.log('Sample channel history:', JSON.stringify(channelHistory.slice(0, 2), null, 2));
  }

  // Format conversation history from Discordant messages with better error handling
  let conversationHistory = 'No previous conversation in this channel';

  try {
    if (channelHistory && channelHistory.length > 0) {
      const validMessages = channelHistory.filter(item => {
        return item.json && 
               item.json.content && 
               typeof item.json.content === 'string' && 
               item.json.content.trim().length > 0;
      });

      if (validMessages.length > 0) {
        conversationHistory = validMessages
          .reverse()
          .map(item => `${item.json.user_id}: ${item.json.content}`)
          .join('\n\n');
      }
    }
  } catch (historyError) {
    console.error('Error processing conversation history:', historyError.message);
    conversationHistory = 'Error loading conversation history (cold start recovery)';
  }

  const now = new Date();
  const currentDate = now.toISOString().split('T')[0];
  const currentTime = now.toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

  const messageCount = channelHistory ? channelHistory.filter(item => item.json && item.json.content && item.json.content.trim()).length : 0;

  const result = {
    ...inputData,
    conversationHistory,
    currentDate,
    currentTime,
    messageCount,
    processingTime: Date.now() - startTime,
    coldStartRecovery: messageCount === 0 && channelHistory && channelHistory.length === 0
  };

  console.log('=== FORMAT CONTEXT SUCCESS ===');
  console.log('Result keys:', Object.keys(result));
  console.log('Message count:', result.messageCount);
  console.log('Channel:', inputData.channelId);
  console.log('Conversation history length:', result.conversationHistory.length);
  console.log('Total processing time:', result.processingTime, 'ms');

  return result;

} catch (error) {
  console.error('=== FORMAT CONTEXT ERROR ===');
  console.error('Error details:', error.message);
  console.error('Error stack:', error.stack);
  console.error('Processing time before error:', Date.now() - startTime, 'ms');

  const inputData = $('Input Processor').first().json;
  return {
    ...inputData,
    conversationHistory: 'Channel history temporarily unavailable (cold start/initialization delay)',
    currentDate: new Date().toISOString().split('T')[0],
    currentTime: new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' }),
    messageCount: 0,
    errorOccurred: true,
    errorMessage: error.message,
    processingTime: Date.now() - startTime,
    coldStartIssue: true
  };
}

// Enhanced Format Response with comprehensive debugging and fallback
console.log('=== FORMAT RESPONSE NODE DEBUG ===');
const startTime = Date.now();

try {
  const aiResponse = $('AI Agent').first().json;
  const contextData = $('Format Context').first().json;

  console.log('AI Response keys:', Object.keys(aiResponse || {}));
  console.log('AI Response data:', JSON.stringify(aiResponse, null, 2));
  console.log('Context Data keys:', Object.keys(contextData || {}));

  // Enhanced response extraction with multiple fallbacks
  let responseText = null;

  // Try primary response fields
  const responseFields = ['output', 'text', 'content', 'message', 'result', 'answer', 'response'];

  for (const field of responseFields) {
    if (aiResponse && aiResponse[field]) {
      responseText = aiResponse[field];
      console.log(`Found response in field '${field}':`, responseText.substring(0, 100));
      break;
    }
  }

  // Check for nested OpenAI-style responses
  if (!responseText && aiResponse?.choices?.[0]?.message?.content) {
    responseText = aiResponse.choices[0].message.content;
    console.log('Found OpenAI-style response:', responseText.substring(0, 100));
  }

  // Check if entire response is a string
  if (!responseText && typeof aiResponse === 'string' && aiResponse.length > 10) {
    responseText = aiResponse;
    console.log('Using entire response as string');
  }

  // If still no response, check for error indicators
  const hasError = aiResponse?.error || aiResponse?.errorOccurred;
  const errorMessage = aiResponse?.errorMessage || aiResponse?.error;

  // Generate fallback response if needed
  if (!responseText || responseText.trim().length === 0) {
    console.log('=== NO RESPONSE FOUND - GENERATING FALLBACK ===');
    console.log('AI Response structure:', JSON.stringify(aiResponse, null, 2));

    const userMessage = contextData?.input || 'your request';
    const currentTime = new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

    if (hasError) {
      responseText = `⚠️ **Processing Issue Detected**

I encountered a technical issue while processing "${userMessage}".

**Error Details**: ${errorMessage || 'Unknown processing error'}
**Time**: ${currentTime}

**Recommended Actions**:
1. Try rephrasing your request
2. Wait 30 seconds and try again
3. Contact support if issue persists

**Note**: Advanced workflow tools are temporarily experiencing issues`;
    } else {
      // Generate contextual fallback based on message content
      const messageType = userMessage.toLowerCase();

      if (messageType.includes('calendar') || messageType.includes('meeting') || messageType.includes('schedule')) {
        responseText = `📅 **Calendar Assistant Response**

I received your calendar request: "${userMessage}"

However, I'm experiencing connectivity issues with the calendar tools right now.

**Alternative Options**:
• Check your calendar directly in your preferred app
• Try the request again in 1-2 minutes
• Use the /test-ai interface for testing

**Time**: ${currentTime}
**Status**: Calendar tools temporarily unavailable`;
      } else if (messageType.includes('search') || messageType.includes('find') || messageType.includes('message')) {
        responseText = `🔍 **Search Assistant Response**

I received your search request: "${userMessage}"

The message search tools are currently experiencing processing delays.

**Try These Alternatives**:
• Use the Discord search feature directly
• Retry your request in a few moments
• Check recent conversation history manually

**Time**: ${currentTime}
**Status**: Search tools temporarily delayed`;
      } else if (messageType.includes('research') || messageType.includes('web') || messageType.includes('find')) {
        responseText = `🌐 **Research Assistant Response**

I received your research request: "${userMessage}"

Web research tools are currently experiencing connection issues.

**Manual Research Suggestions**:
• Try searching directly on Yahoo Finance or Bloomberg
• Check company websites for latest information
• Retry the automated research in a few minutes

**Time**: ${currentTime}
**Status**: Research tools temporarily offline`;
      } else {
        responseText = `🤖 **AI Assistant Response**

I received your request: "${userMessage}"

I'm experiencing technical difficulties with the advanced processing tools right now.

**What I Can Still Help With**:
• General questions and conversation
• Basic business analysis
• Strategic recommendations
• System status updates

**Time**: ${currentTime}
**Status**: Core AI operational, advanced tools temporarily unavailable

**Tip**: Try the /test-ai interface for direct AI interaction`;
      }
    }
  }

  console.log('Final response text length:', responseText?.length || 0);
  console.log('Is test mode:', contextData?.isTestMode);

  let result;

  if (contextData?.isTestMode) {
    result = { 
      output: responseText,
      testMode: true,
      timestamp: new Date().toISOString(),
      processingTime: Date.now() - startTime
    };
  } else {
    result = {
      content: responseText,
      metadata: {
        userId: contextData?.userId || 'unknown',
        channelId: contextData?.channelId || 'unknown',
        sessionId: contextData?.sessionId || 'unknown',
        platform: contextData?.platform || 'discordant',
        messageCount: contextData?.messageCount || 0,
        timestamp: new Date().toISOString(),
        responseSource: 'n8n-workflow',
        processingTime: Date.now() - startTime,
        hadFallback: !aiResponse?.output && !aiResponse?.text,
        debugInfo: {
          aiResponseKeys: Object.keys(aiResponse || {}),
          contextKeys: Object.keys(contextData || {}),
          responseFound: !!responseText
        }
      }
    };
  }

  console.log('=== FORMAT RESPONSE SUCCESS ===');
  console.log('Final result content length:', result.content?.length || result.output?.length || 0);
  console.log('Processing time:', Date.now() - startTime, 'ms');

  return result;

} catch (error) {
  console.error('=== FORMAT RESPONSE ERROR ===');
  console.error('Error details:', error.message);
  console.error('Error stack:', error.stack);
  console.error('Processing time before error:', Date.now() - startTime, 'ms');

  // Provide comprehensive error response
  const currentTime = new Date().toLocaleTimeString('en-US', { timeZone: 'America/New_York' });

  return {
    content: `🚨 **Workflow Processing Error**

A critical error occurred while formatting your response.

**Error**: ${error.message}
**Time**: ${currentTime}
**Processing Duration**: ${Date.now() - startTime}ms

**Recovery Options**:
1. **Retry** your request immediately
2. **Simplify** your request and try again
3. **Contact** system administrator with this error code

**Error Code**: FORMAT_RESPONSE_FAILURE
**Timestamp**: ${new Date().toISOString()}`,
    metadata: {
      userId: 'error-handler',
      channelId: 'error-channel',
      timestamp: new Date().toISOString(),
      errorOccurred: true,
      errorMessage: error.message,
      responseSource: 'n8n-workflow-error',
      processingTime: Date.now() - startTime
    }
