// YouTube Transcript Service via Execute Workflow
const startTime = Date.now();

try {
  const inputData = $input.item.json;
  const youtubeUrl = inputData.youtube_url || inputData.url || inputData.video_url || '';

  console.log(`[YOUTUBE_EXECUTE] Processing: ${youtubeUrl}`);

  if (!youtubeUrl || (!youtubeUrl.includes('youtube.com') && !youtubeUrl.includes('youtu.be'))) {
    throw new Error('Valid YouTube URL is required. Please provide a YouTube video URL.');
  }

  // Execute the YouTube Transcript Service workflow
  const executeResult = await $execution.execute('vkCJIQ3P17JlfSKI', {
    youtube_url: youtubeUrl,
    operation: 'extract',
    source: 'enhanced-ai-agent'
  });

  if (!executeResult || executeResult.error) {
    throw new Error(`Workflow execution failed: ${executeResult?.error || 'Unknown error'}`);
  }

  const result = executeResult.data;
  const processingTime = Date.now() - startTime;

  console.log(`[YOUTUBE_EXECUTE] Success in ${processingTime}ms`);

  // Format comprehensive response
  let response_text = `🎬 **YouTube Video Analysis Complete**\n\n`;

  if (result?.success) {
    response_text += `**📹 Video**: ${result.data?.videoTitle || 'Video processed'}\n`;
    response_text += `**📺 Channel**: ${result.data?.channelName || 'Unknown Channel'}\n`;
    response_text += `**📊 Stats**: ${result.data?.segmentCount || 0} segments, ${result.data?.transcriptLength || 0} characters\n`;
    response_text += `**⚡ Processing**: ${result.data?.cached ? 'Cached (instant)' : 'Real-time extraction'} - ${processingTime}ms\n\n`;

    if (result.data?.transcript) {
      response_text += `**📝 Transcript Preview**:\n`;
      const preview = result.data.transcript.substring(0, 800);
